# Description: Firework Algorithm (FWA) is inspired by the behavior of fireworks exploding in the sky, where each firework represents a potential solution, and the explosion signifies the exploration and exploitation of the search space.
# Code:
# ```python
import numpy as np

class FireworkAlgorithm:
    def __init__(self, budget, dim, num_sparks=5, num_fireworks=10):
        self.budget = budget
        self.dim = dim
        self.num_sparks = num_sparks
        self.num_fireworks = num_fireworks

    def __call__(self, func):
        def explosion(spark, scale):
            return spark + scale * np.random.uniform(-1, 1, self.dim)

        def fitness(firework):
            return func(firework)

        fireworks = np.random.uniform(-5, 5, (self.num_fireworks, self.dim))
        best_firework = fireworks[np.argmin([fitness(firework) for firework in fireworks])]

        for _ in range(self.budget):
            for firework in fireworks:
                for _ in range(self.num_sparks):
                    new_firework = explosion(firework, np.linalg.norm(firework - best_firework))
                    if fitness(new_firework) < fitness(firework):
                        firework = new_firework
                        if fitness(firework) < fitness(best_firework):
                            best_firework = firework

        return best_firework
# ```

# Description: A novel metaheuristic algorithm inspired by the behavior of fireflies in nature for global optimization.
# Code:
# ```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population = np.random.uniform(-5.0, 5.0, (budget, dim))

    def __call__(self, func):
        for _ in range(self.budget):
            for i in range(self.budget):
                for j in range(self.budget):
                    if func(self.population[j]) < func(self.population[i]):
                        self.population[j], self.population[i] = self.population[i], self.population[j]
        return self.population[0]
# ```

# Description: An evolutionary algorithm using a combination of differential evolution and particle swarm optimization for global optimization.
# Code:
# ```python
import numpy as np

class DE_PSO_Optimizer:
    def __init__(self, budget, dim, pop_size=50, F=0.5, CR=0.9, w=0.5, c1=1.5, c2=1.5):
        self.budget = budget
        self.dim = dim
        self.pop_size = pop_size
        self.F = F
        self.CR = CR
        self.w = w
        self.c1 = c1
        self.c2 = c2

    def __call__(self, func):
        def init_population():
            return np.random.uniform(-5.0, 5.0, (self.pop_size, self.dim))

        def mutate(x, population):
            a, b, c = np.random.choice(population, 3, replace=False)
            return np.clip(a + self.F * (b - c), -5.0, 5.0)

        def crossover(x, mutant, mask):
            trial = np.where(np.random.rand(self.dim) < self.CR, mutant, x)
            return trial, mask

        def update_velocity(velocity, x, pbest, gbest):
            cognitive = self.c1 * np.random.rand() * (pbest - x)
            social = self.c2 * np.random.rand() * (gbest - x)
            velocity = self.w * velocity + cognitive + social
            return np.clip(velocity, -1, 1)

        population = init_population()
        fitness = np.array([func(ind) for ind in population])
        pbest = population[np.argmin(fitness)]
        gbest = pbest.copy()
        velocity = np.zeros((self.pop_size, self.dim))

        for _ in range(self.budget - self.pop_size):
            new_population = []
            for i, x in enumerate(population):
                mutant = mutate(x, population)
                trial, _ = crossover(x, mutant, np.random.rand(self.dim) < 0.5)
                new_fitness = func(trial)
                if new_fitness < fitness[i]:
                    population[i] = trial
                    fitness[i] = new_fitness
                if new_fitness < func(pbest):
                    pbest = trial
                if new_fitness < func(gbest):
                    gbest = trial
                velocity[i] = update_velocity(velocity[i], x, pbest, gbest)
            population = population + velocity

        return gbest
# ```

# Description: A novel metaheuristic algorithm inspired by the behavior of social insects to optimize black box functions in a continuous search space.
# Code:
# ```python
import numpy as np

class AntColonyOptimization:
    def __init__(self, budget, dim, colony_size=10, evaporation_rate=0.1, alpha=1.0, beta=2.0):
        self.budget = budget
        self.dim = dim
        self.colony_size = colony_size
        self.evaporation_rate = evaporation_rate
        self.alpha = alpha
        self.beta = beta

    def __call__(self, func):
        def evaluate_solution(solution):
            return func(solution)

        best_solution = np.random.uniform(-5.0, 5.0, self.dim)
        best_fitness = evaluate_solution(best_solution)

        for _ in range(self.budget):
            solutions = [np.random.uniform(-5.0, 5.0, self.dim) for _ in range(self.colony_size)]
            fitness_values = [evaluate_solution(sol) for sol in solutions]

            for i, sol in enumerate(solutions):
                if fitness_values[i] < best_fitness:
                    best_solution = np.copy(sol)
                    best_fitness = fitness_values[i]

        return best_solution
# ```

