import numpy as np

class HybridFireflyLevy:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.alpha = 0.2  # Alpha parameter for firefly movement
        self.beta_min = 0.2  # Minimum beta parameter for firefly attractiveness
        self.beta_max = 1.0  # Maximum beta parameter for firefly attractiveness

    def levy_flight(self, scale=0.1):
        sigma = (gamma(1 + scale) * np.sin(np.pi * scale / 2) / gamma((1 + scale) / 2) / scale / 2 ** ((scale - 1) / 2)) ** (1 / scale)
        u = np.random.normal(0, sigma)
        v = np.random.normal(0, 1)
        step = u / abs(v) ** (1 / scale)
        return step

    def attractiveness(self, i, j, dist):
        return self.beta_min + (self.beta_max - self.beta_min) * np.exp(-self.alpha * dist)

    def move_fireflies(self, fireflies, func):
        for i in range(len(fireflies)):
            for j in range(len(fireflies)):
                if func(fireflies[j]) < func(fireflies[i]):
                    dist = np.linalg.norm(fireflies[j] - fireflies[i])
                    attractiveness = self.attractiveness(i, j, dist)
                    step = self.levy_flight()
                    fireflies[i] += attractiveness * (fireflies[j] - fireflies[i]) + step

    def __call__(self, func):
        fireflies = np.random.uniform(self.lower_bound, self.upper_bound, (self.budget, self.dim))

        for _ in range(self.budget):
            self.move_fireflies(fireflies, func)

        best_index = np.argmin([func(firefly) for firefly in fireflies])
        return fireflies[best_index]
# ```

# Description: Implementing a Hybrid Firefly Algorithm with Evolution Strategies to enhance exploration and exploitation for black box function optimization under a limited evaluation budget.
# Code:
# ```python
import numpy as np

class HybridFireflyEvolutionOptimization:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        firefly_population = [np.random.uniform(self.lower_bound, self.upper_bound, self.dim) for _ in range(self.budget)]
        best_firefly = min(firefly_population, key=func)

        for _ in range(self.budget):
            for i in range(self.budget):
                current_firefly = firefly_population[i]
                for j in range(self.budget):
                    if func(firefly_population[j]) < func(current_firefly):
                        attractiveness = 1 / (1 + np.linalg.norm(firefly_population[j] - current_firefly))
                        current_firefly += 0.5 * attractiveness * (firefly_population[j] - current_firefly)
                if func(current_firefly) < func(best_firefly):
                    best_firefly = current_firefly

        return best_firefly
# ```

# Description: Introducing Collective Quantum Gravitational Optimization (CQGO) by infusing a cooperative strategy among multiple solutions to enhance exploration-exploitation balance.
# Code:
