# import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.alpha = 0.2  # Alpha parameter controls global attractiveness
        self.beta0 = 1.0  # Initial beta parameter
        self.gamma = 0.1  # Gamma parameter for step size control

    def __call__(self, func):
        def attractiveness(x, y):
            return np.exp(-self.beta0 * np.linalg.norm(x - y))

        def move_firefly(firefly, target):
            r = np.linalg.norm(firefly - target)
            beta = self.beta0 * np.exp(-self.gamma * r**2)
            return firefly + self.alpha * (np.random.rand(self.dim) - 0.5) + beta * (target - firefly)

        fireflies = np.random.uniform(-5.0, 5.0, size=(self.budget, self.dim))
        best_firefly = fireflies[np.argmin([func(firefly) for firefly in fireflies])]

        for _ in range(self.budget):
            for i, firefly in enumerate(fireflies):
                for j, other_firefly in enumerate(fireflies):
                    if func(other_firefly) < func(firefly):
                        fireflies[i] = move_firefly(firefly, other_firefly)

            best_firefly = fireflies[np.argmin([func(firefly) for firefly in fireflies])]
            self.beta0 *= 1 - 0.9 * _ / self.budget  # Update beta over iterations

        return best_firefly
