# import numpy as np

class QuantumEA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.qubits = 2 * dim
        self.theta_min = -np.pi
        self.theta_max = np.pi

    def __call__(self, func):
        def measure_population(population, thetas):
            probabilities = [np.abs(np.dot(thetas, individual)) ** 2 for individual in population]
            return np.random.choice(population, p=probabilities)

        def rotate_qubit(qubit, theta):
            return np.dot(np.array([[np.cos(theta/2), -np.sin(theta/2)], [np.sin(theta/2), np.cos(theta/2)]]), qubit)

        population = np.random.uniform(-5.0, 5.0, size=(self.dim, self.dim))
        thetas = np.random.uniform(self.theta_min, self.theta_max, size=(self.qubits, self.dim))

        best_solution = population[np.argmin([func(individual) for individual in population])]

        for _ in range(self.budget):
            selected = measure_population(population, thetas)
            rotated = rotate_qubit(selected, np.random.uniform(self.theta_min, self.theta_max))
            trial = population[np.random.randint(0, self.dim)] + rotated
            if func(trial) < func(selected):
                population[np.argmax([np.array_equal(selected, individual) for individual in population])] = trial
                if func(trial) < func(best_solution):
                    best_solution = np.copy(trial)

        return best_solution
# ```

