import numpy as np

class AdaptiveMutationEvolutionaryAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population = np.random.uniform(-5.0, 5.0, (budget, dim))
        self.mutation_probs = np.ones((budget, dim))

    def __call__(self, func):
        for _ in range(self.budget):
            for i in range(self.budget):
                offspring = self.population[i] + np.random.normal(0, self.mutation_probs[i])
                if func(offspring) < func(self.population[i]):
                    self.population[i] = offspring
                    self.mutation_probs[i] *= 0.9
                else:
                    self.mutation_probs[i] *= 1.1
        return self.population[np.argmin([func(ind) for ind in self.population])]
