import numpy as np

class DE_PSO_Optimizer:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim

    def __call__(self, func):
        population_size = 30
        max_iter = self.budget // population_size
        lb, ub = -5.0, 5.0

        def fitness(x):
            return func(x)

        def within_bounds(x):
            return np.clip(x, lb, ub)

        best_solution = np.random.uniform(lb, ub, self.dim)
        best_fitness = fitness(best_solution)

        for _ in range(max_iter):
            population = np.random.uniform(lb, ub, (population_size, self.dim))
            velocities = np.zeros((population_size, self.dim))
            pbest = population.copy()
            pbest_fitness = np.array([fitness(ind) for ind in pbest])
            gbest_idx = np.argmin(pbest_fitness)
            gbest = pbest[gbest_idx].copy()

            for i in range(population_size):
                r1, r2 = np.random.uniform(0, 1, size=2)
                velocities[i] = velocities[i] + 0.5 * (pbest[i] - population[i]) + 0.8 * (gbest - population[i])
                new_solution = within_bounds(population[i] + velocities[i])
                if fitness(new_solution) < fitness(pbest[i]):
                    pbest[i] = new_solution
                    pbest_fitness[i] = fitness(new_solution)
                    if pbest_fitness[i] < fitness(gbest):
                        gbest = pbest[i]

            if fitness(gbest) < best_fitness:
                best_solution = gbest
                best_fitness = fitness(gbest)

        return best_solution
