import numpy as np

class DEPSO:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 30
        self.cr = 0.9
        self.f = 0.8
        self.w = 0.5
        self.c1 = 2.0
        self.c2 = 2.0

    def __call__(self, func):
        def evaluate_population(population):
            return np.array([func(ind) for ind in population])

        def clip_to_bounds(x):
            return np.clip(x, -5.0, 5.0)

        def de(pbest, current, p1, p2, p3):
            return clip_to_bounds(p1 + self.f * (p2 - p3))

        def pso_update(particle, pbest, gbest):
            r1, r2 = np.random.rand(self.dim), np.random.rand(self.dim)
            v = self.w * particle['velocity'] + self.c1 * r1 * (pbest - particle['position']) + self.c2 * r2 * (gbest - particle['position'])
            particle['position'] = clip_to_bounds(particle['position'] + v)
            particle['velocity'] = v
            return particle

        population = [np.random.uniform(-5.0, 5.0, self.dim) for _ in range(self.pop_size)]
        fitness = evaluate_population(population)
        pbest_idx = np.argmin(fitness)
        gbest_idx = pbest_idx
        gbest = population[pbest_idx].copy()

        for _ in range(self.budget - self.pop_size):
            new_population = []
            for idx, current in enumerate(population):
                p1, p2, p3 = population[np.random.choice([i for i in range(self.pop_size) if i != idx], 3, replace=False)]
                trial = de(population[pbest_idx], current, p1, p2, p3)
                trial_fitness = func(trial)
                if trial_fitness < fitness[idx]:
                    population[idx] = trial
                    fitness[idx] = trial_fitness
                    if trial_fitness < fitness[pbest_idx]:
                        pbest_idx = idx
                        if trial_fitness < fitness[gbest_idx]:
                            gbest_idx = idx
                            gbest = trial.copy()

                population[idx] = pso_update({'position': current, 'velocity': np.zeros(self.dim)}, population[pbest_idx], gbest)

        return gbest
# ```

