import numpy as np

class HybridDESA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 10
        self.mutation_factor = 0.5
        self.crossover_rate = 0.9
        self.temperature = 1.0

    def __call__(self, func):
        def init_population():
            return np.random.uniform(-5.0, 5.0, (self.pop_size, self.dim))

        def mutate(x, pop):
            a, b, c = pop[np.random.choice(len(pop), 3, replace=False)]
            mutant = np.clip(a + self.mutation_factor * (b - c), -5.0, 5.0)
            return mutant

        def crossover(mutant, target):
            crossover_points = np.random.rand(self.dim) < self.crossover_rate
            trial = np.where(crossover_points, mutant, target)
            return trial

        def acceptance_probability(current_val, new_val, temperature):
            if new_val < current_val:
                return 1.0
            return np.exp((current_val - new_val) / temperature)

        population = init_population()
        best_solution = population[np.argmin([func(ind) for ind in population])]
        best_fitness = func(best_solution)

        for _ in range(self.budget):
            candidate = np.array([mutate(ind, population) for ind in population])
            candidate = np.array([crossover(c, t) for c, t in zip(candidate, population)])
            candidate_fitness = np.array([func(ind) for ind in candidate])

            for i in range(self.pop_size):
                if np.random.rand() < acceptance_probability(candidate_fitness[i], func(population[i]), self.temperature):
                    population[i] = candidate[i]

            current_best_fitness = np.min(candidate_fitness)
            if current_best_fitness < best_fitness:
                best_solution = candidate[np.argmin(candidate_fitness)]
                best_fitness = current_best_fitness

            self.temperature *= 0.95  # Cooling schedule

        return best_solution
# ```

