import numpy as np

class AntColonyOptimization:
    def __init__(self, budget, dim, num_ants=10, evaporation_rate=0.1, alpha=1.0, beta=2.0):
        self.budget = budget
        self.dim = dim
        self.num_ants = num_ants
        self.evaporation_rate = evaporation_rate
        self.alpha = alpha
        self.beta = beta

    def __call__(self, func):
        def evaluate_solution(solution):
            return func(solution)

        def initialize_pheromones():
            return np.ones(self.dim)

        def update_pheromones(pheromones, solutions):
            pheromones *= (1 - self.evaporation_rate)
            for sol in solutions:
                pheromones += 1.0 / (1.0 + evaluate_solution(sol))
            return pheromones

        best_solution = np.random.uniform(-5.0, 5.0, self.dim)
        pheromones = initialize_pheromones()

        for _ in range(self.budget):
            solutions = [best_solution + np.random.normal(0, 1, self.dim) for _ in range(self.num_ants)]
            pheromones = update_pheromones(pheromones, solutions)
            best_solution = solutions[np.argmin([evaluate_solution(sol) for sol in solutions])]

        return best_solution
# ```

