import numpy as np

class HybridPSODE:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 30
        self.w = 0.5
        self.c1 = 1.49445
        self.c2 = 1.49445
        self.p_mutate = 0.8
        self.cr = 0.3

    def __call__(self, func):
        def evaluate_population(population):
            return np.array([func(individual) for individual in population])

        def create_initial_population():
            return np.random.uniform(-5.0, 5.0, size=(self.population_size, self.dim))

        def update_velocity(velocity, position, gbest):
            r1 = np.random.random(self.dim)
            r2 = np.random.random(self.dim)
            return self.w * velocity + self.c1 * r1 * (gbest - position) + self.c2 * r2 * (self.pbest - position)

        def mutation(p1, p2, p3):
            return p1 + self.cr * (p2 - p3)

        population = create_initial_population()
        velocities = np.zeros((self.population_size, self.dim))

        pbest = population.copy()
        pbest_values = evaluate_population(pbest)
        gbest_idx = np.argmin(pbest_values)
        gbest = pbest[gbest_idx]

        for _ in range(self.budget - self.population_size):
            for i in range(self.population_size):
                velocities[i] = update_velocity(velocities[i], population[i], gbest)

                candidate = population[i] + velocities[i]
                idxs = np.random.choice(self.population_size, 3, replace=False)
                candidate = mutation(candidate, population[idxs[0]], population[idxs[1]])

                candidate_value = func(candidate)

                if candidate_value < pbest_values[i]:
                    pbest[i] = candidate
                    pbest_values[i] = candidate_value

                    if candidate_value < pbest_values[gbest_idx]:
                        gbest_idx = i
                        gbest = candidate

                population[i] = candidate

        return gbest
