import numpy as np

class FireflyOptimization:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 50
        self.alpha = 0.2
        self.beta_min = 0.2
        self.gamma = 1.0

    def attractiveness(self, x, y):
        return np.exp(-self.gamma * np.linalg.norm(x - y)**2)

    def move_firefly(self, x, best_firefly, func):
        new_x = x + self.alpha * (best_firefly - x) + self.beta_min * (np.random.rand(self.dim) - 0.5)
        if func(new_x) < func(x):
            return new_x
        else:
            return x

    def __call__(self, func):
        fireflies = np.random.uniform(-5.0, 5.0, (self.population_size, self.dim))
        best_firefly = fireflies[np.argmin([func(firefly) for firefly in fireflies])]

        for _ in range(self.budget):
            for i in range(self.population_size):
                for j in range(self.population_size):
                    if func(fireflies[i]) > func(fireflies[j]):
                        fireflies[i] = self.move_firefly(fireflies[i], fireflies[j], func)
                best_firefly = fireflies[np.argmin([func(firefly) for firefly in fireflies])]

        return best_firefly
# ```

