import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.alpha = 0.2
        self.beta = 1.0

    def levy_flight(self):
        beta = 1.5
        sigma = (np.math.gamma(1 + beta) * np.math.sin(np.pi * beta / 2) / (np.math.gamma((1 + beta) / 2) * beta * 2 ** ((beta - 1) / 2))) ** (1 / beta)
        u = np.random.normal(0, sigma)
        v = np.random.normal(0, 1)
        step = u / abs(v) ** (1 / beta)
        return step

    def attractiveness(self, x, y):
        return np.exp(-self.alpha * np.linalg.norm(x - y))

    def move_firefly(self, x, y, distance):
        step = distance * self.levy_flight()
        new_x = x + self.attractiveness(x, y) * (y - x) + step
        return np.clip(new_x, self.lower_bound, self.upper_bound)

    def __call__(self, func):
        firefly = np.random.uniform(self.lower_bound, self.upper_bound, (self.budget, self.dim))
        for _ in range(self.budget):
            for i in range(self.budget):
                for j in range(self.budget):
                    if func(firefly[j]) < func(firefly[i]):
                        distance = np.linalg.norm(firefly[i] - firefly[j])
                        firefly[i] = self.move_firefly(firefly[i], firefly[j], distance)
        return firefly[np.argmin([func(x) for x in firefly])]
