import numpy as np

class HybridDEOppositionBased:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.population_size = 10
        self.scale_factor = 0.8
        self.crossover_rate = 0.9

    def generate_opposite(self, solution):
        return self.lower_bound + self.upper_bound - solution

    def mutate(self, population, target_index):
        candidates = [idx for idx in range(len(population)) if idx != target_index]
        a, b, c = np.random.choice(candidates, 3, replace=False)
        mutant = population[a] + self.scale_factor * (population[b] - population[c])
        return np.clip(mutant, self.lower_bound, self.upper_bound)

    def crossover(self, target, mutant):
        trial = np.copy(target)
        for i in range(len(target)):
            if np.random.rand() < self.crossover_rate:
                trial[i] = mutant[i]
        return trial

    def __call__(self, func):
        population = np.random.uniform(self.lower_bound, self.upper_bound, (self.population_size, self.dim))
        best_solution = population[0]

        for _ in range(self.budget):
            for i in range(self.population_size):
                target = population[i]
                mutant = self.mutate(population, i)
                trial = self.crossover(target, mutant)

                if func(trial) < func(target):
                    population[i] = trial

                if func(trial) < func(best_solution):
                    best_solution = trial

                # Apply opposition-based learning
                opposite_trial = self.generate_opposite(trial)
                if func(opposite_trial) < func(best_solution):
                    best_solution = opposite_trial

        return best_solution
# ```

