        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.hmcr = 0.7
        self.par = 0.4
        self.bandwidth = 0.01

    def __call__(self, func):
        harmony_memory = np.random.uniform(self.lower_bound, self.upper_bound, (self.budget, self.dim))
        best_solution = harmony_memory[np.argmin([func(harmony) for harmony in harmony_memory])]

        for _ in range(self.budget):
            new_harmony = np.random.rand(self.dim)
            for i in range(self.dim):
                if np.random.rand() < self.hmcr:
                    new_harmony[i] = harmony_memory[np.random.randint(self.budget)][i]
                    if np.random.rand() < self.par:
                        new_harmony[i] += np.random.uniform(-1, 1) * self.bandwidth
                else:
                    new_harmony[i] = np.random.uniform(self.lower_bound, self.upper_bound)

            new_fitness = func(new_harmony)

            if new_fitness < func(best_solution):
                best_solution = new_harmony

        return best_solution
