import numpy as np

class AmoebaAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.scale_factor = 1.0
        self.reflection_coeff = 1.0
        self.expansion_coeff = 2.0
        self.contraction_coeff = 0.5

    def __call__(self, func):
        vertices = [np.random.uniform(self.lower_bound, self.upper_bound, self.dim) for _ in range(self.dim + 1)]

        for _ in range(self.budget):
            vertices.sort(key=lambda x: func(x))
            centroid = np.mean(vertices[:-1], axis=0)
            reflected_vertex = centroid + self.reflection_coeff * (centroid - vertices[-1])
            reflected_fitness = func(reflected_vertex)

            if func(vertices[0]) <= reflected_fitness < func(vertices[-2]):
                vertices[-1] = reflected_vertex
            elif reflected_fitness < func(vertices[0]):
                expanded_vertex = centroid + self.expansion_coeff * (reflected_vertex - centroid)
                if func(expanded_vertex) < reflected_fitness:
                    vertices[-1] = expanded_vertex
                else:
                    vertices[-1] = reflected_vertex
            else:
                contracted_vertex = centroid + self.contraction_coeff * (vertices[-1] - centroid)
                contracted_fitness = func(contracted_vertex)
                if contracted_fitness < func(vertices[-1]):
                    vertices[-1] = contracted_vertex
                else:
                    for i in range(1, len(vertices)):
                        vertices[i] = vertices[0] + self.scale_factor * (vertices[i] - vertices[0])

        return vertices[0]
# ```

