    def __call__(self, func):
        population = np.random.uniform(self.lower_bound, self.upper_bound, (self.budget, self.dim))
        intensities = np.zeros(self.budget)

        for _ in range(self.budget):
            for i in range(self.budget):
                intensities[i] = func(population[i])

            for i in range(self.budget):
                for j in range(self.budget):
                    if intensities[j] < intensities[i]:
                        population[i] += self.alpha * (population[j] - population[i]) * self.attractiveness(population[j], population[i])

