# Description: Harmony Search Algorithm (HS) - Harmony Search is a metaheuristic algorithm inspired by the musical process of searching for a perfect state of harmony, incorporating improvisation, memory consideration, and pitch adjustment.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def initialize_harmony_memory(size):
            return np.random.uniform(self.lower_bound, self.upper_bound, (size, self.dim))

        def evaluate_harmony(harmony):
            return func(harmony)

        def improvise_harmony(harmony_memory):
            new_harmony = np.copy(harmony_memory[np.random.randint(len(harmony_memory))])
            for i in range(self.dim):
                if np.random.rand() < 0.5:
                    new_harmony[i] = np.random.uniform(self.lower_bound, self.upper_bound)
            return new_harmony

        harmony_memory = initialize_harmony_memory(5)

        for _ in range(self.budget // 5):
            new_harmony = improvise_harmony(harmony_memory)
            if func(new_harmony) < np.max([func(h) for h in harmony_memory]):
                harmony_memory[np.argmax([func(h) for h in harmony_memory])] = new_harmony

        best_solution = min(harmony_memory, key=lambda x: func(x))
        return best_solution
