import numpy as np

class GQIEA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 30
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.evaluations = 0
        self.alpha = np.random.rand(self.pop_size, self.dim)  # Quantum-inspired coefficients
        self.population = self.qbit_to_real(self.alpha)
        self.fitness = np.full(self.pop_size, float('inf'))
        self.best_global_position = None
        self.best_global_fitness = float('inf')
        self.mutation_prob = 0.1
        self.crossover_prob = 0.7

    def qbit_to_real(self, alpha):
        theta = np.arccos(alpha)  # Quantum bit representation to angle
        return self.lower_bound + (self.upper_bound - self.lower_bound) * (np.cos(theta) ** 2)

    def evaluate(self, func, solution):
        self.evaluations += 1
        return func(solution)

    def select_parents(self):
        idx = np.random.choice(self.pop_size, 2, replace=False)
        return self.population[idx[0]], self.population[idx[1]]

    def crossover(self, parent1, parent2):
        if np.random.rand() < self.crossover_prob:
            mask = np.random.rand(self.dim) < 0.5
            return np.where(mask, parent1, parent2)
        else:
            return parent1

    def mutate(self, offspring):
        for i in range(self.dim):
            if np.random.rand() < self.mutation_prob:
                offspring[i] += np.random.uniform(-0.1, 0.1)
        return np.clip(offspring, self.lower_bound, self.upper_bound)

    def __call__(self, func):
        np.random.seed(42)

        # Initial evaluation
        for i in range(self.pop_size):
            self.fitness[i] = self.evaluate(func, self.population[i])
            if self.fitness[i] < self.best_global_fitness:
                self.best_global_fitness = self.fitness[i]
                self.best_global_position = self.population[i]

        while self.evaluations < self.budget:
            new_population = []

            for _ in range(self.pop_size):
                parent1, parent2 = self.select_parents()
                offspring = self.crossover(parent1, parent2)
                offspring = self.mutate(offspring)
                new_population.append(offspring)

            new_fitness = []
            for i in range(self.pop_size):
                new_fitness_val = self.evaluate(func, new_population[i])
                new_fitness.append(new_fitness_val)
                if new_fitness_val < self.fitness[i]:
                    self.population[i] = new_population[i]
                    self.fitness[i] = new_fitness_val
                    if new_fitness_val < self.best_global_fitness:
                        self.best_global_fitness = new_fitness_val
                        self.best_global_position = new_population[i]

        return self.best_global_position
