import numpy as np

class AQIGA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 50
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.population = np.random.uniform(self.lower_bound, self.upper_bound, (self.pop_size, self.dim))
        self.fitness = np.full(self.pop_size, float('inf'))
        self.best_global_position = None
        self.best_global_fitness = float('inf')
        self.evaluations = 0
        self.alpha = 0.5 # Quantum rotation angle
        self.crossover_rate = 0.8

    def evaluate(self, func, solution):
        self.evaluations += 1
        return func(solution)

    def quantum_superposition(self, x1, x2):
        q1 = np.random.uniform(0, 1, self.dim)
        q2 = np.sqrt(1 - q1**2)
        return q1 * x1 + q2 * x2

    def adaptive_crossover(self, parent, mate):
        if np.random.rand() < self.crossover_rate:
            return self.quantum_superposition(parent, mate)
        else:
            return parent

    def mutation(self, individual):
        scale = np.random.uniform(0, 1)
        mutation = individual + scale * (np.random.uniform(self.lower_bound, self.upper_bound, self.dim) - individual)
        return np.clip(mutation, self.lower_bound, self.upper_bound)

    def __call__(self, func):
        np.random.seed(42)

        for i in range(self.pop_size):
            self.fitness[i] = self.evaluate(func, self.population[i])
            if self.fitness[i] < self.best_global_fitness:
                self.best_global_fitness = self.fitness[i]
                self.best_global_position = self.population[i]

        while self.evaluations < self.budget:
            new_population = []
            for i in range(self.pop_size):
                mate_index = np.random.choice([idx for idx in range(self.pop_size) if idx != i])
                parent = self.population[i]
                mate = self.population[mate_index]

                offspring = self.adaptive_crossover(parent, mate)
                offspring = self.mutation(offspring)
                new_population.append(offspring)

            new_population = np.array(new_population)
            for i in range(self.pop_size):
                trial_fitness = self.evaluate(func, new_population[i])
                if trial_fitness < self.fitness[i]:
                    self.population[i] = new_population[i]
                    self.fitness[i] = trial_fitness
                    if trial_fitness < self.best_global_fitness:
                        self.best_global_fitness = trial_fitness
                        self.best_global_position = new_population[i]

        return self.best_global_position
