import numpy as np

class MEME:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 50
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.population = np.random.uniform(self.lower_bound, self.upper_bound, (self.pop_size, self.dim))
        self.fitness = np.full(self.pop_size, float('inf'))
        self.best_global_position = None
        self.best_global_fitness = float('inf')
        self.evaluations = 0

    def evaluate(self, func, solution):
        self.evaluations += 1
        return func(solution)

    def calculate_diversity(self):
        return np.mean(np.std(self.population, axis=0))

    def update_mutation_factor(self, diversity):
        return max(0.1, min(0.9, 1 - diversity))

    def multi_strategy_mutation(self, xi, x1, x2, x3, diversity):
        F = self.update_mutation_factor(diversity)
        strategies = [
            lambda: xi + F * (x1 - x2 + x3 - x2),
            lambda: self.best_global_position + F * (x1 - x2),
        ]
        strategy = np.random.choice(strategies)
        return strategy()

    def __call__(self, func):
        np.random.seed(42)

        # Initial evaluation
        for i in range(self.pop_size):
            self.fitness[i] = self.evaluate(func, self.population[i])
            if self.fitness[i] < self.best_global_fitness:
                self.best_global_fitness = self.fitness[i]
                self.best_global_position = self.population[i]

        while self.evaluations < self.budget:
            diversity = self.calculate_diversity()
            for i in range(self.pop_size):
                indices = [idx for idx in range(self.pop_size) if idx != i]
                x1, x2, x3 = self.population[np.random.choice(indices, 3, replace=False)]

                mutant = self.multi_strategy_mutation(self.population[i], x1, x2, x3, diversity)
                trial = np.where(np.random.rand(self.dim) < 0.9, mutant, self.population[i])
                trial = np.clip(trial, self.lower_bound, self.upper_bound)

                trial_fitness = self.evaluate(func, trial)
                if trial_fitness < self.fitness[i]:
                    self.population[i] = trial
                    self.fitness[i] = trial_fitness
                    if trial_fitness < self.best_global_fitness:
                        self.best_global_fitness = trial_fitness
                        self.best_global_position = trial

        return self.best_global_position
