import numpy as np

class ShuffledFrogLeaping:
    def __init__(self, budget, dim, num_frogs=20, num_shuffles=5):
        self.budget = budget
        self.dim = dim
        self.num_frogs = num_frogs
        self.num_shuffles = num_shuffles

    def __call__(self, func):
        def generate_random_solution():
            return np.random.uniform(-5.0, 5.0, self.dim)

        def evaluate_solution(solution):
            return func(solution)

        def initialize_population():
            return [generate_random_solution() for _ in range(self.num_frogs)]

        population = initialize_population()
        best_solution = None
        best_fitness = np.inf

        for _ in range(self.budget):
            for i, frog in enumerate(population):
                new_solution = generate_random_solution()
                if evaluate_solution(new_solution) < evaluate_solution(frog):
                    population[i] = new_solution

                if evaluate_solution(frog) < best_fitness:
                    best_solution = frog
                    best_fitness = evaluate_solution(frog)

            for _ in range(self.num_shuffles):
                np.random.shuffle(population)

        return best_solution
