import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim, alpha=0.1, beta_0=1.0, gamma=1.0):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta_0 = beta_0
        self.gamma = gamma

    def __call__(self, func):
        def objective_function(x):
            return func(x)

        fireflies = np.random.uniform(-5.0, 5.0, size=(self.budget, self.dim))
        intensities = np.array([objective_function(firefly) for firefly in fireflies])

        for _ in range(self.budget):
            for i in range(self.budget):
                for j in range(self.budget):
                    if intensities[j] < intensities[i]:
                        beta = self.beta_0 * np.exp(-self.gamma * np.linalg.norm(fireflies[i] - fireflies[j]))
                        fireflies[i] += beta * (fireflies[j] - fireflies[i]) + self.alpha * np.random.uniform(-1, 1, size=self.dim)
                        fireflies[i] = np.clip(fireflies[i], -5.0, 5.0)
                        intensities[i] = objective_function(fireflies[i])

        best_idx = np.argmin(intensities)
        return fireflies[best_idx]
# ```

