import numpy as np

class HybridFireflyGeneticAlgorithm:
    def __init__(self, budget, dim, population_size=50, alpha=0.1, beta0=1.0, gamma=0.1, mutation_rate=0.1):
        self.budget = budget
        self.dim = dim
        self.population_size = population_size
        self.alpha = alpha
        self.beta0 = beta0
        self.gamma = gamma
        self.mutation_rate = mutation_rate

    def __call__(self, func):
        def objective_function(x):
            return func(x)

        def firefly_move(x, attractiveness):
            r = np.random.uniform(-1, 1, size=self.dim)
            x_new = x + attractiveness * np.exp(-self.gamma * np.linalg.norm(r)) * r
            return np.clip(x_new, -5.0, 5.0)

        def crossover(parent1, parent2):
            mask = np.random.randint(0, 2, size=self.dim).astype(bool)
            child = parent1.copy()
            child[mask] = parent2[mask]
            return child

        population = np.random.uniform(-5.0, 5.0, size=(self.population_size, self.dim))
        for _ in range(self.budget):
            attractiveness = self.beta0 * np.exp(-self.alpha * np.arange(self.population_size) / self.population_size)
            for i in range(self.population_size):
                for j in range(self.population_size):
                    if objective_function(population[j]) < objective_function(population[i]):
                        population[i] = firefly_move(population[i], attractiveness[j])
                if np.random.rand() < self.mutation_rate:
                    parent1, parent2 = np.random.choice(population, 2, replace=False)
                    population[i] = crossover(parent1, parent2)

        return population[np.argmin([objective_function(x) for x in population])]
# ```

