import numpy as np

class GASASolver:
    def __init__(self, budget, dim, pop_size=50, mutation_rate=0.1, cooling_rate=0.95):
        self.budget = budget
        self.dim = dim
        self.pop_size = pop_size
        self.mutation_rate = mutation_rate
        self.cooling_rate = cooling_rate

    def __call__(self, func):
        def objective_function(x):
            return func(x)

        def random_neighbor(x):
            return x + np.random.uniform(-0.1, 0.1, size=self.dim)

        def acceptance_probability(curr_cost, new_cost, temp):
            if new_cost < curr_cost:
                return 1.0
            return np.exp((curr_cost - new_cost) / temp)

        population = np.random.uniform(-5.0, 5.0, size=(self.pop_size, self.dim))
        population_cost = np.array([objective_function(x) for x in population])
        best_idx = np.argmin(population_cost)
        best_solution = population[best_idx]

        initial_temp = 1.0
        current_temp = initial_temp
        for _ in range(self.budget - self.pop_size):
            new_population = np.empty_like(population)
            for i in range(self.pop_size):
                candidate = random_neighbor(population[i])
                candidate_cost = objective_function(candidate)
                ap = acceptance_probability(population_cost[i], candidate_cost, current_temp)
                if np.random.rand() < ap:
                    population[i], population_cost[i] = candidate, candidate_cost
                if candidate_cost < objective_function(best_solution):
                    best_solution = candidate

            new_population[0] = best_solution
            for i in range(1, self.pop_size):
                parent1, parent2 = np.random.choice(self.pop_size, 2, replace=False)
                crossover_point = np.random.randint(0, self.dim)
                new_population[i] = np.concatenate((population[parent1][:crossover_point], population[parent2][crossover_point:]))

            population = new_population
            current_temp *= self.cooling_rate

        return best_solution
# ```

