import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim, alpha=0.2, beta0=1.0, gamma=1.0):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta0 = beta0
        self.gamma = gamma

    def __call__(self, func):
        def attractiveness(distance):
            return self.beta0 * np.exp(-self.gamma * distance ** 2)

        def move(firefly, best_firefly):
            distance = np.linalg.norm(firefly - best_firefly)
            attractiveness_level = attractiveness(distance)
            return firefly + self.alpha * (attractiveness_level * (best_firefly - firefly)) + np.random.uniform(-1, 1, self.dim)

        fireflies = np.random.uniform(-5.0, 5.0, size=(self.budget, self.dim))
        fireflies_costs = np.array([func(x) for x in fireflies])
        best_firefly_idx = np.argmin(fireflies_costs)
        best_firefly = fireflies[best_firefly_idx]

        for _ in range(self.budget):
            for i in range(self.budget):
                if fireflies_costs[i] < fireflies_costs[best_firefly_idx]:
                    best_firefly_idx = i
                    best_firefly = fireflies[i]

                fireflies[i] = move(fireflies[i], best_firefly)
                fireflies[i] = np.clip(fireflies[i], -5.0, 5.0)
                fireflies_costs[i] = func(fireflies[i])

        return best_firefly
# ```

