# import numpy as np

class DESolver:
    def __init__(self, budget, dim, cr=0.5, f=0.5):
        self.budget = budget
        self.dim = dim
        self.cr = cr
        self.f = f

    def __call__(self, func):
        def objective_function(x):
            return func(x)

        def mutation(current, population, f):
            candidates = np.random.choice(population, size=3, replace=False)
            donor = candidates[0] + f * (candidates[1] - candidates[2])
            return np.clip(donor, -5.0, 5.0)

        def crossover(target, donor, cr):
            trial = np.copy(target)
            for i in range(len(target)):
                if np.random.rand() < cr:
                    trial[i] = donor[i]
            return trial

        population = np.random.uniform(-5.0, 5.0, size=(self.budget, self.dim))

        for _ in range(self.budget):
            for i in range(len(population)):
                idxs = [idx for idx in range(len(population)) if idx != i]
                target = population[i]
                donor = mutation(target, population[idxs], self.f)
                trial = crossover(target, donor, self.cr)
                if objective_function(trial) < objective_function(target):
                    population[i] = trial

        return population[np.argmin([objective_function(x) for x in population])]
