class OppDEAMSolver:
    def __init__(self, budget, dim, pop_size=50, f=0.5, cr=0.9, opp_rate=0.1):
        self.budget = budget
        self.dim = dim
        self.pop_size = pop_size
        self.f = f
        self.cr = cr
        self.opp_rate = opp_rate

    def __call__(self, func):
        def objective_function(x):
            return func(x)

        def opposition_based(x):
            return 2.0 * 5.0 - x

        population = np.random.uniform(-5.0, 5.0, size=(self.pop_size, self.dim))
        population_cost = np.array([objective_function(x) for x in population])

        for _ in range(self.budget - self.pop_size):
            for i in range(self.pop_size):
                idxs = [idx for idx in range(self.pop_size) if idx != i]
                a, b, c = population[np.random.choice(idxs, 3, replace=False)]
                mutant = population[a] + self.f * (population[b] - population[c])
                for j in range(self.dim):
                    if np.random.rand() > self.cr:
                        mutant[j] = population[i][j]
                mutant = np.clip(mutant, -5.0, 5.0)

                mutant_cost = objective_function(mutant)
                if mutant_cost < population_cost[i]:
                    population[i] = mutant
                    population_cost[i] = mutant_cost
                if np.random.rand() < self.opp_rate:
                    population[i] = opposition_based(population[i])

        best_idx = np.argmin(population_cost)
        return population[best_idx]
