import numpy as np

class GeneticSimulatedAnnealingSolver:
    def __init__(self, budget, dim, population_size=50, mutation_rate=0.1, temperature=1.0):
        self.budget = budget
        self.dim = dim
        self.population_size = population_size
        self.mutation_rate = mutation_rate
        self.temperature = temperature

    def __call__(self, func):
        def objective_function(x):
            return func(x)

        def mutate(x):
            return x + np.random.uniform(-1.0, 1.0, size=self.dim) * self.mutation_rate

        population = np.random.uniform(-5.0, 5.0, size=(self.population_size, self.dim))
        population_cost = np.array([objective_function(x) for x in population])
        g_best_idx = np.argmin(population_cost)
        g_best = population[g_best_idx]

        for _ in range(self.budget - self.population_size):
            new_population = []
            for i in range(self.population_size):
                offspring = mutate(population[i])
                cost_offspring = objective_function(offspring)
                if cost_offspring < population_cost[i]:
                    population[i], population_cost[i] = offspring, cost_offspring
                else:
                    delta = (cost_offspring - population_cost[i]) / self.temperature
                    if np.random.rand() < np.exp(-delta):
                        population[i], population_cost[i] = offspring, cost_offspring
                new_population.append(population[i])

            population = np.array(new_population)

            g_best_idx = np.argmin(population_cost)
            if population_cost[g_best_idx] < objective_function(g_best):
                g_best = population[g_best_idx]

            self.temperature *= 0.95  # Cooling schedule

        return g_best
# ```

