# Description: A novel optimization algorithm named Evolutionary Hybrid Search (EHS) that combines evolutionary strategies with hybrid search techniques to efficiently explore and exploit the search space for black box optimization.
# Code:
# ```python
import numpy as np

class EHS:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 10
        self.crossover_rate = 0.8
        self.mutation_rate = 0.1
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(self.lb, self.ub, (self.population_size, self.dim))

        def crossover(parent1, parent2):
            mask = np.random.choice([0, 1], size=self.dim)
            child1 = np.where(mask, parent1, parent2)
            child2 = np.where(mask, parent2, parent1)
            return child1, child2

        def mutate(individual):
            mutant = individual + np.random.normal(0, 0.1, self.dim)
            return np.clip(mutant, self.lb, self.ub)

        population = initialize_population()
        best_solution = population[np.argmin([func(individual) for individual in population])]

        for _ in range(self.budget - self.population_size):
            parents = np.random.choice(range(self.population_size), size=(self.population_size // 2, 2), replace=False)

            for parent1_idx, parent2_idx in parents:
                child1, child2 = crossover(population[parent1_idx], population[parent2_idx])

                if func(child1) < func(population[parent1_idx]):
                    population[parent1_idx] = child1

                if func(child2) < func(population[parent2_idx]):
                    population[parent2_idx] = child2

            for i in range(self.population_size):
                if np.random.rand() < self.mutation_rate:
                    population[i] = mutate(population[i])

            best_solution = population[np.argmin([func(individual) for individual in population])]

        return best_solution
# ```

