# Description: A Hybrid Firefly Optimization Algorithm (HFOA) which combines the movement behavior of fireflies with a local search strategy to efficiently explore and exploit the search space for black box optimization.
# Code:
# ```python
import numpy as np

class HFOA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.alpha = 0.1  # Damping coefficient
        self.beta = 0.6   # Absorption coefficient
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(self.lb, self.ub, (self.population_size, self.dim))

        def attractiveness(distance_sq):
            return np.exp(-self.beta * distance_sq)

        population = initialize_population()
        best_solution = population[np.argmin([func(individual) for individual in population)]

        for _ in range(self.budget - self.population_size):
            for i in range(self.population_size):
                for j in range(self.population_size):
                    if func(population[j]) < func(population[i]):
                        distance_sq = np.sum((population[i] - population[j])**2)
                        attractiveness_ij = attractiveness(distance_sq)
                        population[i] += attractiveness_ij * (population[j] - population[i])

                population[i] = np.clip(population[i], self.lb, self.ub)

                if func(population[i]) < func(best_solution):
                    best_solution = population[i]

        return best_solution
# ```

