# Description: An Advanced Hybrid Particle Swarm Optimization Algorithm that leverages both particle swarm optimization and differential evolution to efficiently explore and exploit the search space for black box optimization.
# Code:
# ```python
import numpy as np

class AdvancedHybridPSO:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.c1 = 1.49445
        self.c2 = 1.49445
        self.w = 0.729
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(self.lb, self.ub, (self.population_size, self.dim))

        def update_velocity(particle, velocity, p_best, g_best):
            r1 = np.random.rand(self.dim)
            r2 = np.random.rand(self.dim)
            return self.w * velocity + self.c1 * r1 * (p_best - particle) + self.c2 * r2 * (g_best - particle)

        def differential_evolution(particle, p_best, g_best):
            F = 0.5
            CR = 0.9
            candidate = particle
            for _ in range(3):
                idxs = np.random.choice(np.arange(self.population_size), 3, replace=False)
                x1, x2, x3 = population[idxs]
                donor = x1 + F * (x2 - x3)
                j_rand = np.random.randint(self.dim)
                for j in range(self.dim):
                    if np.random.rand() < CR or j == j_rand:
                        candidate[j] = donor[j]
                if func(candidate) < func(particle):
                    particle = candidate
                    if func(candidate) < func(p_best):
                        p_best = candidate
                    if func(candidate) < func(g_best):
                        g_best = candidate
            return particle, p_best, g_best

        population = initialize_population()
        p_best_idx = np.argmin([func(individual) for individual in population])
        p_best = population[p_best_idx].copy()
        g_best = p_best.copy()

        for _ in range(self.budget - self.population_size):
            for i in range(self.population_size):
                velocity = update_velocity(population[i], velocity, p_best, g_best)
                population[i] = np.clip(population[i] + velocity, self.lb, self.ub)
                population[i], p_best, g_best = differential_evolution(population[i], p_best, g_best)

        return g_best
