import numpy as np

class DBA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 10
        self.lb = -5.0
        self.ub = 5.0
        self.trial_limit = 3

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(self.lb, self.ub, (self.population_size, self.dim))

        def mutate(solution, best_solution):
            mutant = solution + np.random.uniform(-1, 1, size=self.dim) * (solution - best_solution)
            return np.clip(mutant, self.lb, self.ub)

        population = initialize_population()
        best_solution = population[np.argmin([func(individual) for individual in population])]

        for _ in range(self.budget - self.population_size):
            for i in range(self.population_size):
                solution = population[i]
                candidate = mutate(solution, best_solution)

                if func(candidate) < func(solution):
                    population[i] = candidate

                    if func(candidate) < func(best_solution):
                        best_solution = candidate

            # Abandon poor solutions
            for i in range(self.population_size):
                if np.random.rand() < 1 / (1 + np.exp(-1 * (func(population[i]) - func(best_solution)))):
                    population[i] = best_solution

        return best_solution
