# import numpy as np

class FireflyOptimization:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 10
        self.alpha = 0.2  # Damping coefficient
        self.beta0 = 1.0  # Initial attraction coefficient
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(self.lb, self.ub, (self.population_size, self.dim))

        def attractiveness(distance):
            return self.beta0 * np.exp(-self.alpha * distance)

        population = initialize_population()
        best_solution = population[np.argmin([func(individual) for individual in population])]

        for _ in range(self.budget - self.population_size):
            for i in range(self.population_size):
                for j in range(self.population_size):
                    if func(population[j]) < func(population[i]):
                        distance = np.linalg.norm(population[i] - population[j])
                        attractiveness_ij = attractiveness(distance)
                        population[i] += attractiveness_ij * (population[j] - population[i]) + np.random.uniform(-1, 1, self.dim)
                        population[i] = np.clip(population[i], self.lb, self.ub)

            # Update best solution
            current_best = population[np.argmin([func(individual) for individual in population])]
            if func(current_best) < func(best_solution):
                best_solution = current_best

        return best_solution
