import numpy as np

class BacterialForagingOptimization:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 10
        self.swim_length = 4
        self.rep_steps = 6
        self.elim_disp_steps = 8
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(self.lb, self.ub, (self.population_size, self.dim))

        def tumble(current_position, step_size):
            return current_position + step_size * np.random.uniform(-1, 1, self.dim)

        def chemotaxis_step(position, step_size):
            new_position = tumble(position, step_size)
            new_position = np.clip(new_position, self.lb, self.ub)
            return new_position

        population = initialize_population()
        best_solution = population[np.argmin([func(individual) for individual in population])

        for _ in range(self.budget - self.population_size):
            swim_step = np.random.choice([0, 1], self.population_size)

            for i in range(self.population_size):
                current_position = population[i].copy()
                for _ in range(self.swim_length):
                    new_position = chemotaxis_step(current_position, swim_step[i])
                    if func(new_position) < func(population[i]):
                        population[i] = new_position

            # Reproduction
            sorted_indices = np.argsort([func(individual) for individual in population])
            for i in range(self.rep_steps):
                population[sorted_indices[i]] = population[sorted_indices[-1]] + np.random.normal(0, 1, self.dim)

            # Elimination-Dispersal
            for i in range(self.elim_disp_steps):
                random_index = np.random.randint(self.population_size)
                population[random_index] = np.random.uniform(self.lb, self.ub, self.dim)

            best_solution = population[np.argmin([func(individual) for individual in population])]

        return best_solution
