# Description: A Differential Evolution Algorithm with Dual Population Strategy for Black Box Optimization, combining two populations to enhance diversity and convergence in the search process.
# Code:
# ```python
import numpy as np

class DifferentialEvolution:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 10
        self.F = 0.5   # Differential weight
        self.CR = 0.9  # Crossover rate
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(self.lb, self.ub, (self.population_size, self.dim))

        population = initialize_population()
        for _ in range(self.budget):
            new_population = np.zeros((self.population_size, self.dim))
            for i in range(self.population_size):
                a, b, c = np.random.choice(self.population_size, 3, replace=False)
                r = np.random.randint(self.dim)
                for j in range(self.dim):
                    if np.random.rand() < self.CR or j == r:
                        new_population[i, j] = population[a, j] + self.F * (population[b, j] - population[c, j])
                    else:
                        new_population[i, j] = population[i, j]
                new_population[i] = np.clip(new_population[i], self.lb, self.ub)
                if func(new_population[i]) < func(population[i]):
                    population[i] = new_population[i]

        return population[np.argmin([func(individual) for individual in population])]
# ```
