# Description: Evolutionary Algorithm (EA) with Adaptive Strategy Refinement for Black Box Optimization
# Code:
# ```python
import numpy as np

class EvolutionaryAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.population = np.random.uniform(self.lower_bound, self.upper_bound, (self.budget, self.dim))
        self.fitness = np.full(self.budget, np.inf)

    def __call__(self, func):
        prob_refine = 0.35
        for t in range(self.budget):
            mutated_population = np.copy(self.population)
            for i in range(self.budget):
                if np.random.rand() < prob_refine:
                    mutated_population[i] = self.refine_strategy(mutated_population[i])
            fitness_values = np.apply_along_axis(func, 1, mutated_population)
            idx = np.argmin(fitness_values)
            if fitness_values[idx] < np.max(self.fitness):
                self.fitness[np.argmax(self.fitness)] = fitness_values[idx]
                self.population[np.argmax(self.fitness)] = mutated_population[idx]
        return self.population[np.argmin(self.fitness)]

    def refine_strategy(self, individual):
        return individual + np.random.normal(0, 0.1, self.dim)
# ```

