import numpy as np

class FireflyAlgorithmLevyFlights:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0
        self.alpha = 0.2
        self.beta = 1.0
        self.population = np.random.uniform(self.lower_bound, self.upper_bound, (self.budget, self.dim))

    def levy_flight(self):
        sigma_u = (np.math.gamma(1 + self.beta) * np.math.sin(np.pi * self.beta / 2) / (np.math.gamma((1 + self.beta) / 2) * self.beta * 2 ** ((self.beta - 1) / 2))) ** (1 / self.beta)
        sigma_v = 1
        u = np.random.normal(0, sigma_u, self.dim)
        v = np.random.normal(0, sigma_v, self.dim)
        step = u / (np.abs(v) ** (1 / self.beta))
        return step

    def attractiveness(self, i, j, func):
        return np.exp(-self.alpha * np.linalg.norm(self.population[i] - self.population[j])**2)

    def move_fireflies(self, func):
        for i in range(self.budget):
            for j in range(self.budget):
                if func(self.population[i]) > func(self.population[j]):
                    step = self.levy_flight()
                    self.population[i] += self.attractiveness(i, j, func) * step

    def __call__(self, func):
        for _ in range(self.budget):
            self.move_fireflies(func)

        return self.population[np.argmin([func(ind) for ind in self.population])]
