        population = initialize_population()
        costs = evaluate_population(population)
        best_idx = np.argmin(costs)
        best_solution = population[best_idx]

        for _ in range(self.budget - self.pop_size):
            selected_parents = population[np.random.choice(range(self.pop_size), 2, replace=False)]
            child = crossover(selected_parents[0], selected_parents[1])
            mutated_child = mutate(child)
            child_cost = func(mutated_child)
            if child_cost < costs[best_idx]:
                population[best_idx] = mutated_child
                costs[best_idx] = child_cost
