import numpy as np

class HS_ADE_Optimizer:
    def __init__(self, budget, dim, harmony_memory_size=10, pitch_adjusting_rate=0.1, mutation_factor=0.5, crossover_prob=0.8):
        self.budget = budget
        self.dim = dim
        self.harmony_memory_size = harmony_memory_size
        self.pitch_adjusting_rate = pitch_adjusting_rate
        self.mutation_factor = mutation_factor
        self.crossover_prob = crossover_prob

    def __call__(self, func):
        harmony_memory = np.random.uniform(-5.0, 5.0, (self.harmony_memory_size, self.dim))
        best_solution = harmony_memory[np.argmin([func(harmony) for harmony in harmony_memory])]
        for _ in range(self.budget):
            new_harmony = np.zeros((1, self.dim))
            for j in range(self.dim):
                if np.random.rand() < self.crossover_prob:
                    new_harmony[0, j] = harmony_memory[np.random.randint(self.harmony_memory_size), j]
                else:
                    new_harmony[0, j] = best_solution[j]

                if np.random.rand() < self.mutation_factor:
                    new_harmony[0, j] += np.random.uniform(-self.pitch_adjusting_rate, self.pitch_adjusting_rate)

                if func(new_harmony) < func(best_solution):
                    best_solution = new_harmony

                if func(new_harmony) < func(harmony_memory[-1]):
                    harmony_memory[-1] = new_harmony
                    harmony_memory = harmony_memory[np.argsort([func(harmony) for harmony in harmony_memory])]

        return best_solution
# ```

