import numpy as np
from scipy.stats import levy

class HybridFireflyES_Optimizer:
    def __init__(self, budget, dim, alpha=0.2, beta0=1.0, population_size=50, mutation_rate=0.1):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta0 = beta0
        self.population_size = population_size
        self.mutation_rate = mutation_rate

    def levy_flight(self, current_position):
        return current_position + self.alpha * levy.rvs(size=self.dim)

    def __call__(self, func):
        population = np.random.uniform(-5.0, 5.0, (self.population_size, self.dim))
        best_solution = population[np.argmin([func(individual) for individual in population])]

        for _ in range(self.budget):
            for i in range(self.population_size):
                current_position = population[i]
                attractor = population[np.argmin([func(individual) for individual in population])]
                beta = self.beta0 * np.exp(-0.1 * _)
                new_position = current_position + beta * (attractor - current_position) + self.levy_flight(current_position)

                if np.random.rand() < self.mutation_rate:
                    mutated_position = new_position + np.random.normal(0, 0.1, self.dim)
                    new_position = mutated_position if func(mutated_position) < func(new_position) else new_position

                if func(new_position) < func(population[i]):
                    population[i] = new_position
                    if func(new_position) < func(best_solution):
                        best_solution = new_position

        return best_solution
# ```

