import numpy as np

class HS_OL_Optimizer:
    def __init__(self, budget, dim, harmony_memory_size=20, harmony_memory_consideration_rate=0.95, pitch_adjusting_rate=0.5):
        self.budget = budget
        self.dim = dim
        self.harmony_memory_size = harmony_memory_size
        self.harmony_memory_consideration_rate = harmony_memory_consideration_rate
        self.pitch_adjusting_rate = pitch_adjusting_rate

    def opposition_based_learning(self, solution):
        return 2.0 * np.mean(solution) - solution

    def __call__(self, func):
        harmony_memory = np.random.uniform(-5.0, 5.0, (self.harmony_memory_size, self.dim))
        best_solution = harmony_memory[np.argmin([func(individual) for individual in harmony_memory])]

        for _ in range(self.budget):
            new_harmony = []
            for i in range(self.harmony_memory_size):
                if np.random.rand() < self.harmony_memory_consideration_rate:
                    new_solution = np.clip(harmony_memory[i] + np.random.uniform(-1, 1, self.dim), -5.0, 5.0)
                    new_solution_cost = func(new_solution)
                    if new_solution_cost < func(best_solution):
                        best_solution = new_solution
                else:
                    new_solution = harmony_memory[np.random.randint(0, self.harmony_memory_size)]

                if np.random.rand() < self.pitch_adjusting_rate:
                    new_solution = self.opposition_based_learning(new_solution)

                new_harmony.append(new_solution)

            harmony_memory = np.array(new_harmony)

        return best_solution
# ```

