import numpy as np

class Adaptive_DE_FA_Optimizer:
    def __init__(self, budget, dim, population_size=50, mutation_factor=0.8, crossover_prob=0.9, absorption_coefficient=0.2):
        self.budget = budget
        self.dim = dim
        self.population_size = population_size
        self.mutation_factor = mutation_factor
        self.crossover_prob = crossover_prob
        self.absorption_coefficient = absorption_coefficient

    def __call__(self, func):
        population = np.random.uniform(-5.0, 5.0, (self.population_size, self.dim))
        best_solution = population[np.argmin([func(individual) for individual in population])]
        for _ in range(self.budget):
            for i in range(self.population_size):
                firefly = population[i]
                for j in range(self.population_size):
                    if func(population[j]) < func(firefly):
                        firefly += self.absorption_coefficient * (population[j] - firefly) + self.mutation_factor * np.random.uniform(-1, 1, self.dim)
                        firefly = np.clip(firefly, -5.0, 5.0)
                        if func(firefly) < func(population[i]):
                            population[i] = firefly
                            if func(firefly) < func(best_solution):
                                best_solution = firefly
        return best_solution
