import numpy as np

class FF_DE_Metaheuristic:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 30
        self.max_iter = budget // self.pop_size
        self.F = 0.5
        self.CR = 0.9
        self.alpha = 0.2
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def clip(x):
            return np.clip(x, self.lb, self.ub)

        def evaluate(x):
            return func(clip(x))

        def create_population():
            return np.random.uniform(self.lb, self.ub, (self.pop_size, self.dim))

        def FA(x, gamma_best):
            attractiveness = np.zeros(self.pop_size)
            for i in range(self.pop_size):
                for j in range(self.pop_size):
                    if evaluate(x[j]) < evaluate(x[i]):
                        attractiveness[i] += (1 / (1 + np.linalg.norm(x[i] - x[j])))

            for i in range(self.pop_size):
                x[i] += self.alpha * (attractiveness[i] * (gamma_best - x[i]) + np.random.uniform(-1, 1, self.dim))
                x[i] = clip(x[i])

            return x

        def DE(x, target_idx):
            a, b, c = np.random.choice(np.delete(np.arange(self.pop_size), target_idx), 3, replace=False)
            mutant = clip(x[a] + self.F * (x[b] - x[c]))
            crossover = np.random.rand(self.dim) < self.CR
            trial = np.where(crossover, mutant, x[target_idx])
            return trial

        population = create_population()
        best_solution = population[0]

        for i in range(self.max_iter):
            for j in range(self.pop_size):
                trial = DE(population, j)
                if evaluate(trial) < evaluate(population[j]):
                    population[j] = trial

            population = FA(population, best_solution)
            best_solution = min(population, key=evaluate)

        return best_solution
