import numpy as np

class FA_DE_Metaheuristic:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 30
        self.max_iter = budget // self.pop_size
        self.alpha = 0.2
        self.beta_min = 0.2
        self.gamma = 1.0
        self.F = 0.5
        self.CR = 0.9
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def clip(x):
            return np.clip(x, self.lb, self.ub)

        def evaluate(x):
            return func(clip(x))

        def create_population():
            return np.random.uniform(self.lb, self.ub, (self.pop_size, self.dim))

        def FA(x, target_idx, g_best):
            attractiveness = self.alpha * np.exp(-self.gamma * np.linalg.norm(x - g_best))
            x_new = x + attractiveness * (g_best - x) + self.beta_min * (np.random.rand(self.dim) - 0.5)
            return clip(x_new)

        def DE(x, target_idx):
            a, b, c = np.random.choice(np.delete(np.arange(self.pop_size), target_idx), 3, replace=False)
            mutant = clip(x[a] + self.F * (x[b] - x[c]))
            crossover = np.random.rand(self.dim) < self.CR
            trial = np.where(crossover, mutant, x[target_idx])
            return trial

        population = create_population()
        best_solution = population[0]

        for i in range(self.max_iter):
            for j in range(self.pop_size):
                trial = DE(population, j)
                if evaluate(trial) < evaluate(population[j]):
                    population[j] = trial

            g_best = population[np.argmin([evaluate(xi) for xi in population])]

            for j in range(self.pop_size):
                population[j] = FA(population[j], j, g_best)

        return g_best
# ```

