import numpy as np

class HarmonySearchMetaheuristic:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 30
        self.max_iter = budget // self.pop_size
        self.hmcr = 0.7
        self.par = 0.3
        self.bw = 0.01
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def clip(x):
            return np.clip(x, self.lb, self.ub)

        def evaluate(x):
            return func(clip(x))

        def create_population():
            return np.random.uniform(self.lb, self.ub, (self.pop_size, self.dim))

        def improvise(harmony_memory):
            new_solution = np.zeros(self.dim)
            for d in range(self.dim):
                if np.random.rand() < self.hmcr:
                    idx = np.random.choice(self.pop_size)
                    new_solution[d] = harmony_memory[idx, d]
                    if np.random.rand() < self.par:
                        new_solution[d] += np.random.normal(0, self.bw)
                else:
                    new_solution[d] = np.random.uniform(self.lb, self.ub)
            return clip(new_solution)

        harmony_memory = create_population()
        best_solution = harmony_memory[0]

        for _ in range(self.max_iter):
            new_solution = np.array([improvise(harmony_memory) for _ in range(self.pop_size)])
            harmony_memory = np.where(np.array([evaluate(sol) for sol in new_solution]) < np.array([evaluate(sol) for sol in harmony_memory]), new_solution, harmony_memory)
            best_solution = harmony_memory[np.argmin([evaluate(sol) for sol in harmony_memory])]

        return best_solution
