import numpy as np

class Cuckoo_Firefly_Metaheuristic:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 30
        self.max_iter = budget // self.pop_size
        self.pa = 0.25
        self.alpha = 0.5
        self.beta0 = 1.0
        self.gamma = 1.0
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def clip(x):
            return np.clip(x, self.lb, self.ub)

        def evaluate(x):
            return func(clip(x))

        def levy_flight(size):
            sigma1 = (np.math.gamma(1 + self.alpha) * np.math.sin(np.pi * self.alpha / 2) / np.math.gamma((1 + self.alpha) / 2) * 2 ** ((self.alpha - 1) / 2)) ** (1 / self.alpha)
            sigma2 = 1
            u = np.random.normal(0, sigma1, size)
            v = np.random.normal(0, sigma2, size)
            step = u / np.abs(v) ** (1 / self.alpha)
            return step

        def create_population():
            return np.random.uniform(self.lb, self.ub, (self.pop_size, self.dim))

        population = create_population()
        best_solution = population[0]

        for i in range(self.max_iter):
            for j in range(self.pop_size):
                step_size = levy_flight(self.dim)
                new_cuckoo = clip(population[j] + step_size * self.beta0)
                if evaluate(new_cuckoo) < evaluate(population[j]):
                    population[j] = new_cuckoo

            for j in range(self.pop_size):
                firefly = population[j]
                for k in range(self.pop_size):
                    if evaluate(population[k]) < evaluate(firefly):
                        distance = np.linalg.norm(firefly - population[k])
                        attractiveness = self.beta0 * np.exp(-self.gamma * distance**2)
                        firefly = clip(firefly + attractiveness * (population[k] - firefly))

                if evaluate(firefly) < evaluate(best_solution):
                    best_solution = firefly

        return best_solution
# ```

