        def create_population():
            return np.random.uniform(self.lb, self.ub, (self.pop_size, self.dim))

        def move_ff(firefly, best_firefly):
            attraction = self.beta_min + (self.alpha - self.beta_min) * np.exp(-self.gamma * np.linalg.norm(firefly - best_firefly))
            noise = np.random.uniform(-self.bandwidth, self.bandwidth, self.dim)
            return clip(firefly + attraction * (best_firefly - firefly) + noise)

        def improvise_hs(harmony):
            new_harmony = harmony.copy()
            for i in range(self.dim):
                if np.random.rand() < 0.5:
                    new_harmony[i] = np.random.uniform(self.lb, self.ub)
            return new_harmony

        population = create_population()
        best_solution = population[0]

        for _ in range(self.max_iter):
            for i in range(self.pop_size):
                for j in range(self.pop_size):
                    if evaluate(population[j]) < evaluate(population[i]):
                        population[i] = move_ff(population[i], population[j])

                new_harmony = improvise_hs(population[i])
                if evaluate(new_harmony) < evaluate(population[i]):
                    population[i] = new_harmony

                if evaluate(population[i]) < evaluate(best_solution):
                    best_solution = population[i]

        return best_solution
