import numpy as np

class FA_EHS_Metaheuristic:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 30
        self.max_iter = budget // self.pop_size
        self.alpha = 0.5
        self.beta_min = 0.2
        self.beta_max = 1.0
        self.bandwidth = 0.01
        self.hmcr = 0.9
        self.par = 0.7
        self.lb = -5.0
        self.ub = 5.0

    def __call__(self, func):
        def clip(x):
            return np.clip(x, self.lb, self.ub)

        def evaluate(x):
            return func(clip(x))

        def create_population():
            return np.random.uniform(self.lb, self.ub, (self.pop_size, self.dim))

        def FA(fireflies):
            for _ in range(self.max_iter):
                for i in range(self.pop_size):
                    for j in range(self.pop_size):
                        if evaluate(fireflies[j]) < evaluate(fireflies[i]):
                            beta = self.beta_min + (self.beta_max - self.beta_min) * np.exp(-self.alpha * np.linalg.norm(fireflies[i] - fireflies[j])**2)
                            fireflies[i] += beta * (fireflies[j] - fireflies[i]) + self.bandwidth * np.random.normal(0, 1, self.dim)
                            fireflies[i] = clip(fireflies[i])
                fireflies = sorted(fireflies, key=lambda x: evaluate(x))

            return fireflies[0]

        def EHS(harmonies):
            for _ in range(self.max_iter):
                new_harmonies = []
                for harmony in harmonies:
                    new_harmony = harmony.copy()
                    for d in range(self.dim):
                        if np.random.rand() < self.hmcr:
                            new_harmony[d] = np.clip(harmony[d] + np.random.uniform(-1, 1) * self.par, self.lb, self.ub)
                    new_harmonies.append(new_harmony)
                harmonies = sorted(harmonies + new_harmonies, key=lambda x: evaluate(x))[:self.pop_size]

            return harmonies[0]

        population = create_population()
        best_solution = population[0]

        for i in range(self.max_iter):
            best_solution = FA(population)
            best_solution = EHS([best_solution] + population)[0]

        return best_solution
# ```

