            c2 = self.c2_min + (self.c2_max - self.c2_min) * np.random.rand()
            step_size = 0.1

            for j in range(self.pop_size):
                trial = self.DE(population, j, F, CR)
                if levy_evaluate(trial, step_size) < levy_evaluate(population[j], step_size):
                    population[j] = trial

            best_solution = self.PSO(population, best_solution, w, c1, c2)

