# Description: Implementing a Simulated Annealing metaheuristic algorithm for optimization tasks with a temperature-based acceptance probability to escape local optima.
# Code:
# ```python
import numpy as np

class SimulatedAnnealingAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.init_temperature = 100.0
        self.cooling_rate = 0.95
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def accept(self, current_fitness, new_fitness, temperature):
        if new_fitness < current_fitness:
            return True
        return np.random.rand() < np.exp((current_fitness - new_fitness) / temperature)

    def perturb(self, x):
        return x + np.random.uniform(-0.1, 0.1, size=self.dim)

    def __call__(self, func):
        current_solution = np.random.uniform(self.lower_bound, self.upper_bound, size=self.dim)
        current_fitness = func(current_solution)
        temperature = self.init_temperature

        for _ in range(self.budget):
            new_solution = self.perturb(current_solution)
            new_fitness = func(new_solution)

            if self.accept(current_fitness, new_fitness, temperature):
                current_solution = new_solution
                current_fitness = new_fitness

            temperature *= self.cooling_rate

        return current_solution
```

# Description: Utilizing an improved Firefly Algorithm with adaptive attractiveness scaling for effective global optimization in noisy environments.
# Code:
# ```python
import numpy as np

class ImprovedFireflyAlgorithm:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.alpha = 0.2
        self.beta_min = 0.2
        self.beta_max = 2.0
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def attractiveness(self, r):
        return self.beta_min + (self.beta_max - self.beta_min) * np.exp(-self.alpha * r**2)

    def move_fireflies(self, fireflies, func):
        for i in range(len(fireflies)):
            for j in range(len(fireflies)):
                if func(fireflies[j]) < func(fireflies[i]):
                    r = np.linalg.norm(fireflies[i] - fireflies[j])
                    beta = self.attractiveness(r)
                    fireflies[i] += beta * (fireflies[j] - fireflies[i]) + np.random.uniform(-1, 1, self.dim)

        return fireflies

    def __call__(self, func):
        fireflies = np.random.uniform(self.lower_bound, self.upper_bound, size=(self.population_size, self.dim))

        for _ in range(self.budget):
            fireflies = self.move_fireflies(fireflies, func)

        best_idx = np.argmin([func(individual) for individual in fireflies])
        return fireflies[best_idx]
